import React from 'react';
import { Drizzle } from 'drizzle';
import options from './drizzleOptions';
import ReplyForm from './ReplyForm';

const drizzle = new Drizzle(options);

export default class SingleTweet extends React.Component {
  constructor(props) {
    super();
  }
  async componentDidMount() {
    // call the func here
    const tweetData = await this.getData(
      this.props.address,
      this.props.index,
      this.props.replyIndex
    );
    this.setState({ tweetData });
    console.log(this.state);
  }

  getData = async (address, index, replyIndex = 0) => {
    if (replyIndex - 1 < 0) {
      replyIndex = 0;
    }

    const result = await drizzle.contracts.Twittor.methods
      .getReply(address, index, replyIndex - 1)
      .call();

    console.log(result, 'GETDATA RESULT');
    return result;
  };

  render() {
    return (
      <div>
        <h1>Reply</h1>
        <p>Address: {this.props.address}</p>
        {/* <p>Block Num: {this.state[2]}</p> */}
        {/* <p>Tweet: {this.state[0]}</p> */}
      </div>
    );
  }
}
